const LazyLoader={loadModule: async function(modulePath){try{const module=await import(modulePath);return module;}catch(error){console.error('Failed to load module:',modulePath,error);return null;}},loadOnDemand: function(trigger,modulePath,callback){document.addEventListener(trigger,async()=>{const module=await this.loadModule(modulePath);if(module && callback){callback(module);}});}};window.LazyLoader=LazyLoader;