class ChatButtonIntegrator{constructor(){this.chatButtons=new Map();this.init();}init(){if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',()=>{this.integrateExistingButtons();});}else{this.integrateExistingButtons();}}integrateExistingButtons(){setTimeout(()=>{this.integrateChatButtons();},1000);}integrateChatButtons(){this.integrateQuantumChatButton();this.integrateAIAssistantButton();this.integrateOtherChatButtons();}integrateQuantumChatButton(){const floatingButton=document.getElementById('floating-chat-button');if(floatingButton && window.floatingButtonsManager){floatingButton.className='';floatingButton.style.cssText='';window.floatingButtonsManager.registerButton('quantum-chat',{element: floatingButton,preferredPosition: 'bottom-right-1',type: 'chat',priority: 5,title: '量子智能助手',icon: 'fas fa-comments'});console.log('✅ 量子聊天按钮已集成到管理器');}}integrateAIAssistantButton(){const aiAssistantBtn=document.getElementById('aiAssistantBtn');if(aiAssistantBtn && window.floatingButtonsManager){const computedStyle=window.getComputedStyle(aiAssistantBtn);if(computedStyle.position==='fixed'){aiAssistantBtn.className='';aiAssistantBtn.style.cssText='';window.floatingButtonsManager.registerButton('ai-assistant',{element: aiAssistantBtn,preferredPosition: 'bottom-right-1',type: 'chat',priority: 4,title: 'AI智能助手',icon: 'fas fa-robot'});console.log('✅ AI助手按钮已集成到管理器');}}}integrateOtherChatButtons(){const chatToggleBtn=document.getElementById('chatToggleBtn');const chatButton=document.getElementById('chatButton');[chatToggleBtn,chatButton].forEach((btn,index)=>{if(btn && window.floatingButtonsManager){const computedStyle=window.getComputedStyle(btn);if(computedStyle.position==='fixed'){btn.className='';btn.style.cssText='';window.floatingButtonsManager.registerButton(`chat-${index}`,{element: btn,preferredPosition: 'bottom-right-1',type: 'chat',priority: 3-index,title: '聊天助手',icon: 'fas fa-comment-dots'});console.log(`✅ 聊天按钮 ${index}已集成到管理器`);}}});}createUnifiedChatButton(){if(this.chatButtons.size===0){const unifiedButton=document.createElement('button');unifiedButton.id='unified-chat-button';unifiedButton.innerHTML='<i class="fas fa-comments"></i>';document.body.appendChild(unifiedButton);if(window.floatingButtonsManager){window.floatingButtonsManager.registerButton('unified-chat',{element: unifiedButton,preferredPosition: 'bottom-right-1',type: 'chat',priority: 10,title: '智能聊天助手',icon: 'fas fa-comments',onClick: this.handleUnifiedChatClick.bind(this)});console.log('✅ 统一聊天按钮已创建');}}}handleUnifiedChatClick(){const chatContainer=document.getElementById('chat-container');const chatWidget=document.getElementById('chatWidget');const aiChatContainer=document.getElementById('ai-chat-container');if(chatContainer){if(window.quantumChatIntegrator && typeof window.quantumChatIntegrator.showChatInterface==='function'){window.quantumChatIntegrator.showChatInterface();}}else if(chatWidget){chatWidget.classList.remove('hidden');}else if(aiChatContainer){if(window.aiChat && typeof window.aiChat.showChat==='function'){window.aiChat.showChat();}}else{this.createBasicChatInterface();}}createBasicChatInterface(){const chatInterface=document.createElement('div');chatInterface.id='basic-chat-interface';chatInterface.innerHTML=` <div class="fixed bottom-20 right-20 w-80 h-96 bg-white rounded-lg shadow-2xl border z-50"> <div class="bg-blue-600 text-white p-4 rounded-t-lg"> <div class="flex justify-between items-center"> <h3 class="font-semibold">智能助手</h3> <button onclick="this.parentElement.parentElement.parentElement.remove()" class="text-white hover:text-gray-200"> <i class="fas fa-times"></i> </button> </div> </div> <div class="p-4 h-64 overflow-y-auto"> <div class="mb-4 p-3 bg-gray-100 rounded-lg"> 您好！我是AlingAi Pro的智能助手。有什么可以帮助您的吗？ </div> </div> <div class="p-4 border-t"> <div class="flex gap-2"> <input type="text" placeholder="输入消息..." class="flex-1 p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"> <button class="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"> 发送 </button> </div> </div> </div> `;document.body.appendChild(chatInterface);console.log('✅ 基本聊天界面已创建');}}window.chatButtonIntegrator=new ChatButtonIntegrator();console.log('💬 聊天按钮集成管理器已加载');