(function(){function waitForPageLoad(){return new Promise((resolve)=>{if(document.readyState==='complete'){resolve();}else{window.addEventListener('load',resolve);}});}function checkResources(){const checks={'Three.js': typeof THREE !=='undefined','CppCodeAnimation类': typeof CppCodeAnimation !=='undefined','动画容器': document.getElementById('cpp-animation-container')!==null,'CSS样式': document.querySelector('link[href*="cpp-animation.css"]')!==null};for(const [name,status] of Object.entries(checks)){}return Object.values(checks).every(status=> status);}async function verifyAnimation(){await waitForPageLoad();const resourcesOK=checkResources();if(!resourcesOK){console.error('❌ 资源检查失败，无法初始化动画');return false;}const container=document.getElementById('cpp-animation-container');if(!container){console.error('❌ 动画容器未找到');return false;}try{if(window.cppAnimation){return true;}window.cppAnimation=new CppCodeAnimation('cpp-animation-container');window.cppAnimation.addMouseInteraction();console.log('🎯 动画位置:',{容器: container.getBoundingClientRect(),页面中的位置: container.offsetTop+'px from top'});return true;}catch(error){console.error('❌ 动画初始化失败:',error);return false;}}window.debugCppAnimation={restart:()=>{if(window.cppAnimation){window.cppAnimation.restart();}},check: verifyAnimation,info:()=>{if(window.cppAnimation){}}};verifyAnimation().then(success=>{if(success){console.log('💡 调试命令: window.debugCppAnimation.restart(),window.debugCppAnimation.info()');}else{console.error('💥 C++动画集成验证失败！');}});})();