class SystemErrorHandler{constructor(){this.errorLog=[];this.maxLogSize=100;this.suppressedErrors=new Set();this.errorCounts=new Map();this.initializeErrorHandling();}initializeErrorHandling(){window.addEventListener('error',(event)=>{this.handleError({type: 'javascript',message: event.message,filename: event.filename,lineno: event.lineno,colno: event.colno,error: event.error,timestamp: new Date().toISOString()});});window.addEventListener('unhandledrejection',(event)=>{this.handleError({type: 'promise',message: event.reason?.message || 'Promise rejection',reason: event.reason,timestamp: new Date().toISOString()});});this.addSuppression('The AudioContext was not allowed to start');this.addSuppression('Could not load content for');this.addSuppression('DevTools failed to load source map');this.addSuppression('net::ERR_HTTP_RESPONSE_CODE_FAILURE');}handleError(errorInfo){const errorKey=this.getErrorKey(errorInfo);if(this.shouldSuppressError(errorInfo)){return;}const count=this.errorCounts.get(errorKey)|| 0;this.errorCounts.set(errorKey,count+1);if(count < 3){this.logError(errorInfo);this.errorLog.push(errorInfo);if(this.errorLog.length > this.maxLogSize){this.errorLog.shift();}}}getErrorKey(errorInfo){return `${errorInfo.type}:${errorInfo.message}:${errorInfo.filename}:${errorInfo.lineno}`;}shouldSuppressError(errorInfo){return Array.from(this.suppressedErrors).some(pattern=> errorInfo.message?.includes(pattern));}addSuppression(pattern){this.suppressedErrors.add(pattern);}logError(errorInfo){const prefix=errorInfo.type==='promise' ? 'üî∏' : 'üîπ';console.group(`${prefix}Á≥ªÁªüÈîôËØØ [${errorInfo.type}]`);console.error('Ê∂àÊÅØ:',errorInfo.message);if(errorInfo.filename){console.error('Êñá‰ª∂:',errorInfo.filename,'Ë°å:',errorInfo.lineno);}if(errorInfo.error?.stack){console.error('Â†ÜÊ†à:',errorInfo.error.stack);}console.groupEnd();}getErrorSummary(){const summary={totalErrors: this.errorLog.length,errorsByType:{},topErrors: []};this.errorLog.forEach(error=>{summary.errorsByType[error.type]=(summary.errorsByType[error.type] || 0)+1;});const sortedErrors=Array.from(this.errorCounts.entries()).sort((a,b)=> b[1]-a[1]).slice(0,5);summary.topErrors=sortedErrors.map(([key,count])=>({key,count}));return summary;}}class ConsoleOptimizer{constructor(){this.messageCache=new Map();this.rateLimits=new Map();this.messageCount=0;this.initializeOptimization();}initializeOptimization(){this.originalConsole={log: console.log,warn: console.warn,error: console.error,info: console.info};console.log=this.createRateLimitedLogger('log');console.warn=this.createRateLimitedLogger('warn');console.error=this.createRateLimitedLogger('error');console.info=this.createRateLimitedLogger('info');}createRateLimitedLogger(level){return(...args)=>{const message=args.join(' ');const key=`${level}:${message}`;if(this.shouldRateLimit(key)){return;}this.originalConsole[level](...args);this.messageCount++;};}shouldRateLimit(key){const now=Date.now();const limit=this.rateLimits.get(key);if(!limit){this.rateLimits.set(key,{count: 1,lastTime: now});return false;}if(now-limit.lastTime < 1000 && limit.count >=3){return true;}if(now-limit.lastTime > 1000){limit.count=1;limit.lastTime=now;}else{limit.count++;}return false;}getStats(){return{totalMessages: this.messageCount,rateLimitedMessages: this.rateLimits.size,cacheSize: this.messageCache.size};}reset(){this.messageCache.clear();this.rateLimits.clear();this.messageCount=0;}}class PerformanceMonitor{constructor(){this.metrics={};this.observers=[];this.initializeMonitoring();}initializeMonitoring(){if('PerformanceObserver' in window){const navObserver=new PerformanceObserver((list)=>{const entries=list.getEntries();entries.forEach(entry=>{if(entry.entryType==='navigation'){this.metrics.navigation={domContentLoaded: entry.domContentLoadedEventEnd-entry.domContentLoadedEventStart,loadComplete: entry.loadEventEnd-entry.loadEventStart,firstPaint: entry.fetchStart,timestamp: Date.now()};}});});try{navObserver.observe({entryTypes: ['navigation']});this.observers.push(navObserver);}catch(e){console.warn('ÂØºËà™ÊÄßËÉΩÁõëÊéß‰∏çÂèØÁî®');}const resourceObserver=new PerformanceObserver((list)=>{const entries=list.getEntries();const slowResources=entries.filter(entry=> entry.duration > 1000 && entry.name.includes('.js'));if(slowResources.length > 0){console.warn('üêå Ê£ÄÊµãÂà∞Âä†ËΩΩÁºìÊÖ¢ÁöÑËµÑÊ∫ê:',slowResources.map(r=>({name: r.name.split('/').pop(),duration: Math.round(r.duration)+'ms'})));}});try{resourceObserver.observe({entryTypes: ['resource']});this.observers.push(resourceObserver);}catch(e){console.warn('ËµÑÊ∫êÊÄßËÉΩÁõëÊéß‰∏çÂèØÁî®');}}}getPerformanceReport(){const report={timestamp: new Date().toISOString(),metrics: this.metrics,memory: this.getMemoryUsage(),timing: this.getPageTiming()};return report;}getMemoryUsage(){if('memory' in performance){return{used: Math.round(performance.memory.usedJSHeapSize/1024/1024)+'MB',total: Math.round(performance.memory.totalJSHeapSize/1024/1024)+'MB',limit: Math.round(performance.memory.jsHeapSizeLimit/1024/1024)+'MB'};}return null;}getPageTiming(){if('timing' in performance){const timing=performance.timing;return{pageLoad: timing.loadEventEnd-timing.navigationStart,domReady: timing.domContentLoadedEventEnd-timing.navigationStart,firstByte: timing.responseStart-timing.navigationStart};}return null;}cleanup(){this.observers.forEach(observer=> observer.disconnect());this.observers=[];}}window.systemErrorHandler=new SystemErrorHandler();window.consoleOptimizer=new ConsoleOptimizer();window.performanceMonitor=new PerformanceMonitor();window.debugUtils={getErrorSummary:()=> window.systemErrorHandler.getErrorSummary(),getConsoleStats:()=> window.consoleOptimizer.getStats(),getPerformanceReport:()=> window.performanceMonitor.getPerformanceReport(),resetConsole:()=> window.consoleOptimizer.reset(),suppressError:(pattern)=> window.systemErrorHandler.addSuppression(pattern),showSystemStatus:()=>{console.group('üîç Á≥ªÁªüÁä∂ÊÄÅÊä•Âëä');console.log('ÈîôËØØÁªüËÆ°:',window.debugUtils.getErrorSummary());console.log('ÊéßÂà∂Âè∞ÁªüËÆ°:',window.debugUtils.getConsoleStats());console.log('ÊÄßËÉΩÊä•Âëä:',window.debugUtils.getPerformanceReport());console.groupEnd();}};window.addEventListener('beforeunload',()=>{window.performanceMonitor.cleanup();});console.log('üîß ÂèØÁî®Â∑•ÂÖ∑: window.debugUtils.showSystemStatus()');