<?php class PhpSyntaxFixer { private $rootDir; private $logFile; private $backupDir; public function __construct($rootDir = 'public') { $this->rootDir = $rootDir; $this->logFile = 'logs/php_fixer.log'; $this->backupDir = 'public/backups/php_fixes_' . date('YmdHis']; if (!file_exists('logs')) { mkdir('logs', 0777, true]; } if (!file_exists($this->backupDir)) { mkdir($this->backupDir, 0777, true]; } } }
